const emojis = ["😄", "🌟", "💜", "🚀", "🎉", "💨", "🍀", "👨‍💻", "😎", "🔥", "🌈", "🦄", "✨", "💻", "🧠"];
const emojiCount = 50;
const emojiElements = [];
const speed = 2;
const container = document.getElementById('emoji-container');

// Create emoji elements and add them to the container
for (let i = 0; i < emojiCount; i++) {
    const emoji = document.createElement('div');
    emoji.className = 'emoji';
    emoji.textContent = emojis[Math.floor(Math.random() * emojis.length)];
    container.appendChild(emoji);
    emojiElements.push({
        element: emoji,
        x: Math.random() * (window.innerWidth - 48),
        y: Math.random() * (window.innerHeight - 48),
        xSpeed: (Math.random() - 0.5) * speed * 2,
        ySpeed: (Math.random() - 0.5) * speed * 2
    });
}

function animate() {
    emojiElements.forEach(emoji => {
        // Update position
        emoji.x += emoji.xSpeed;
        emoji.y += emoji.ySpeed;

        // Bounce off walls
        if (emoji.x <= 0) {
            emoji.x = 0;
            emoji.xSpeed *= -1;
        } else if (emoji.x >= container.clientWidth - 48) {
            emoji.x = container.clientWidth - 48;
            emoji.xSpeed *= -1;
        }

        if (emoji.y <= 0) {
            emoji.y = 0;
            emoji.ySpeed *= -1;
        } else if (emoji.y >= container.clientHeight - 48) {
            emoji.y = container.clientHeight - 48;
            emoji.ySpeed *= -1;
        }

        // Update element style
        emoji.element.style.transform = `translate(${emoji.x}px, ${emoji.y}px)`;
    });
    requestAnimationFrame(animate);
}

animate();

// Adjust emojis on window resize
window.addEventListener('resize', () => {
    emojiElements.forEach(emoji => {
        emoji.x = Math.random() * (container.clientWidth - 48);
        emoji.y = Math.random() * (container.clientHeight - 48);
    });
});
